import React, { useState, useEffect } from "react";

function Checkout(props){

  console.log("In Checkout");

/* 
  async function fetchData() {
    const { data } = 
      fetch('https://localhost:9000/orders', requestOptions)
        .then(response => response.json())
        .then(data => setPostId(data.id));
    setFetchedData(data);
  }

  const handleSubmit = e => {
    e.preventDefault();
    fetchData();
  }

*/

  const [order, setOrder] = useState(props.checkoutBook)

  const submit = e => {
    e.preventDefault()
    fetch('https://localhost:9000/orders', {
      method: 'POST',
      body: JSON.stringify({ order }),
      headers: { 'Content-Type': 'application/json' },
    })
      .then(res => res.json())
      .then(json => setOrder(json.order))
  }


  return (
    <div>
      <h2>Checkout</h2>
      <p>Book: {props.checkoutBook}</p>
      <form onSubmit={handleSubmit}>
        <div class="form-group">
            <label for="id">ID</label>
            <input name="id" defaultValue={props.checkoutBook} />
        </div>
        <button type="submit">Submit</button>
      </form>


      <form id="payment-form">
          <div id="card-element">
          </div>

          <div id="card-errors" role="alert"></div>

          <button id="submit">Pay</button>
        </form>
    </div>
  )
}

export default Checkout;
