import React, { Component } from "react";
import { loadStripe } from "@stripe/stripe-js";
import { Elements } from "@stripe/react-stripe-js";
import "./App.css";


class App extends Component {
    constructor(props) {
        super(props);
        this.state = { books: [] };
        //this.books = [];
    }

/*
    callAPI() {

        var response = fetch('http://localhost:9000/orders')
          .then(function(response) {return response.json();})
          .then(function(responseJson) {
            var clientSecret = responseJson.client_secret;
            // Call stripe.confirmCardPayment() with the client secret.
          });
    }


    getBooks() {
      var response = fetch('http://localhost:9000/books')
          .then(function(response) {return response.json();})
          .then(function(responseJson) {
            var books = responseJson;
          });
    }
    */

    getBooks() {
      fetch("http://localhost:9000/books")
            .then(res => res.json())
            .then(resJson => this.setState({ books: resJson }))
            .catch(err => err);
    }



    componentDidMount() {
        this.getBooks();
        //this.callAPI();
    }



    render() {
        return (
            <div className="App">
                <p className="App-intro">{"Books"+this.state.books}</p>
            </div>
        );
    }
}

export default App;
