import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
//import BookContainer from './BookContainer'
import MainContainer from './MainContainer'
import Checkout from './Checkout'


function App () {
    const [checkoutBook, setBook] = React.useState('');
    const [orders, setOrders] = useState([]);

    useEffect(() => {
      fetchOrders() // Fetch orders
    }, []);

    const fetchOrders = () => {
      fetch('http://localhost:9000/orders', {
        method: 'GET',
      })
        .then((res) => res.json())
        .then((result) => setOrders(result.rows))
        .catch((err) => console.log('error'))
    }

    const updateBook = (chosenBook) => {
      setBook(chosenBook);
    }

    return (
      <div className="App">
        <Router>
          <Switch>
            <Route path="/" exact component={() => 
              <MainContainer onBookSelection={updateBook} orders={orders}/>} />
            <Route path="/checkout" exact component={() => 
              <Checkout checkoutBook={checkoutBook} />} />
            <Route path='*' exact component={() => 
              <MainContainer onBookSelection={updateBook} />} />
          </Switch>
        </Router>

      </div>
    );
}

export default App;
